CREATE TABLE IF NOT EXISTS `#__jkcustomfields_categories` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(255) NOT NULL,  `alias` varchar(255) NOT NULL,  `description` text NOT NULL,  `access` int(11) NOT NULL,  `state` tinyint(1) NOT NULL,  `created` datetime NOT NULL,  `created_by` int(11) NOT NULL,  `modified` datetime NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__jkcustomfields_items` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(255) NOT NULL,  `catid` int(11) NOT NULL,  `description` text NOT NULL,  `thumb` varchar(255) DEFAULT NULL,  `image` varchar(255) NOT NULL,  `short_desc` text NOT NULL,  `created` datetime NOT NULL,  `state` tinyint(1) NOT NULL,  `icon_fb` varchar(255) NOT NULL,  `created_by` int(11) NOT NULL,  `icon_tw` varchar(255) DEFAULT NULL,  `icon_ins` varchar(255) DEFAULT NULL,  `customfields` text NOT NULL,  `subtitle` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__jkcustomfields_config` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `params` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__jkcustomfields_customfields` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(255) NOT NULL,  `catid` int(11) NOT NULL,  `description` text NOT NULL,  `fields` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;