his->sidebar)): ?>        <div id="j-sidebar-container" class="span2">            <?php echo $this->sidebar; ?>        </div>        <div id="j-main-container" class="span10">            <?php else : ?>            <div id="j-main-container">                <?php endif;?>                <div>                    <?php echo JLayoutHelper::render('joomla.searchtools.default', array('view' => $this)); ?>                </div>                <div class="clearfix"> </div>                <table class="table table-striped" id="itemList">                    <thead>            <tr>                <th width="1%" class="nowrap center hidden-phone jksort">                        <?php echo JHtml::_('grid.sort', '', 'a.ordering', $listDirn, $listOrder);//null, 'asc', 'JGRID_HEADING_ORDERING', 'icon-menu-2' ?>                </th>                <th width="1%" class="hidden-phone">                    <?php echo JHtml::_('grid.checkall'); ?>                </th>                <th width="5%">                    <?php echo JHtml::_('grid.sort', 'JPUBLISHED', 'a.state', $listDirn, $listOrder); ?>                </th>                <th class='left'>                    <?php echo JHtml::_('grid.sort',  'COM_JKCUSTOMFIELDS_TITLE', 'a.title', $listDirn, $listOrder); ?>                </th>                                <th class='left'>                    <?php echo JHtml::_('grid.sort',  'Category', 'category', $listDirn, $listOrder); ?>                </th>                <th class='left'>                    <?php echo JHtml::_('grid.sort',  'Date Created', 'created', $listDirn, $listOrder); ?>                </th>                	                               <th width="1%" class="nowrap center hidden-phone">                    <?php echo JHtml::_('grid.sort', 'JGRID_HEADING_ID', 'a.id', $listDirn, $listOrder); ?>                </th>